mapper.select:
SELECT p.m_id,p.product_no, p.product_name, p.product_price, p.product_detail ,p.product_stock, p.product_dlvyfee, p.option_no ,p.category_local_no, p.category_type_no ,pf.productfile_name, pf.productfile_path, pf.productfile_no FROM product p LEFT OUTER JOIN productfile pf ON p.product_no = pf.product_no
AND m_id = #{m_id}
AND p.product_no = #{product_no}
ORDER BY p.product_no ASC
