mapper.select:
select recipe_re_no, recipe_re_content, m_id, recipe_re_date	
from recipe_reply
where recipe_no = #{recipe_no}

mapper.insert:
insert into recipe_reply (
							recipe_no,
							recipe_re_no,
							recipe_re_content,
							recipe_re_pwd,
							m_id					
	 )
values (					#{recipe_no}
						, seq_recipe_re_no.nextval
						, #{recipe_re_content}
						, #{recipe_re_pwd}
						, #{m_id}

)
